mapper.select:
SELECT o.id, o.user_id, o.total_price, o.`status`, o.user_order_num, o.created_at, o.updated_at, u.username
FROM `order` o
LEFT JOIN user u ON o.user_id = u.id


        o.user_id = #{userId}



            AND o.id = #{id}


            o.id = #{id}




            AND u.username LIKE '%' || #{username} || '%'


            u.username LIKE '%' || #{username} || '%'




            AND o.`status` = #{status}


            o.`status` = #{status}



ORDER BY o.created_at DESC



    LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE `order`
SET `status` = #{status}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM `order` o
LEFT JOIN user u ON o.user_id = u.id


        o.user_id = #{userId}



            AND o.id = #{id}


            o.id = #{id}




            AND u.username LIKE '%' || #{username} || '%'


            u.username LIKE '%' || #{username} || '%'




            AND o.`status` = #{status}


            o.`status` = #{status}
