mapper.update:
UPDATE rating
SET rating = #{rating}, 
    updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT AVG(rating) as average_rating
FROM rating
WHERE book_id = #{bookId}

SELECT id, user_id, book_id, rating, created_at, updated_at
FROM rating
WHERE user_id = #{userId} AND book_id = #{bookId}

SELECT COUNT(*) as rating_count
FROM rating
WHERE book_id = #{bookId}

SELECT id, user_id, book_id, rating, created_at, updated_at
FROM rating
WHERE id = #{id}
